var sound = RD_UK_HOUSE_WURLITZER_3;
insertMediaSection(sound, 1, 1.5, 2.5, 3.0);

// Sounds in einen neuen Beat remixen
var s1 = HIPHOP_FUNKBEAT_001;
makeBeat(s1, 1, 1, "0++-0---0---0---");
var s2 = [Y01_HI_HATS_1, Y01_OPEN_HI_HATS_1];
makeBeat(s2, 2, 1, "--10--10--100010");

// Bongo triplets
makeBeat(IRCA_OS_BONGO_HIGH_OPEN, 1, 1, "000", 3);
