makeBeatSlice(sound, 1, 1, beat, eighths, 8);

// Vom Benutzer bereitgestellter Beat String
var beat = readInput("Enter a beat, like 0-0-0");
makeBeat(OS_CLAP01, 1, 1, beat);

// Auswahl eines beat strings
beatChoices = ["0000", "0-0-", "00--"]
var beatIndex = multiChoiceInput("Choose a beat string", beatChoices);
makeBeat(OS_CLAP01, 1, 1, beatChoices[beatIndex]);

// Sound board
var soundChoices = [MILKNSIZZ_ADIOS_DRUMS, MILKNSIZZ_ADIOS_SHAKEY_SHAKER, MILKNSIZZ_ADIOS_COWBELLS, MILKNSIZZ_ADIOS_LATIN_808];
var chosenSounds = multiChoiceInput("Choose your sounds", soundChoices, true);
for (var i = 0; i < chosenSounds.length; i++) {
    var choice = chosenSounds[i]
    var sound = soundChoices[choice]
    fitMedia(sound, i+1, 1, 5)
}

// Ersetze OS_SNARE01 mit OS_CLAP01
var sounds = [OS_KICK01, OS_SNARE01, OS_CLOSEDHAT01];
